import { createSlic } from '@reduxjs/toolkit';
import axios from 'axios';
import { RootState } from '.';

type dataApodTypes = {
  title: string,
  explanation?: string,
  url: string
  date: string;
 }

type dataApod = {
  apData: dataApodTypes;
  newDate: string;
  input: string;
  favorite: dataApodTypes;
  loading: boolean;
  error: boolean;
}

const initialState = {
  apData: {},
  newDate: '',
  input: '',
  favorite: {},
  loading: true,
  error: false,
} as dataApod;

// export const fetchFavorites = createAsyncThunk(
