import { createSlice } from '@reduxjs/toolkit';
import { RootState } from '.';

type dataApodTypes = {
  title: string,
  explanation?: string,
  url: string
  date: string;
 }

type dataApod = {
  apData: dataApodTypes;
  newDate: string;
  input: string;
  favorite: dataApodTypes;
  loading: boolean;
  error: boolean;
}

