/* eslint-disable no-param-reassign */
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { RootState } from '.';

type dataApodTypes = {
  title: string,
  explanation?: string,
  url: string
  date: string;
 }

type dataApod = {
  apData: dataApodTypes;
  newDate: string;
  input: string;
  favorite: dataApodTypes;
  loading: boolean;
  error: boolean;
}

const initialState = {
  apData: {},
  newDate: '',
  input: '',
  favorite: {},
  loading: true,
  error: false,
} as dataApod;

export const fetchFavorites = createAsyncThunk(
  'favorites/fetchFavorites',
  async () => {
    const dataFavs = await axios
      .get('http://localhost:5000/favorites');
    console.log(dataFavs);
    return dataFavs.data;
  },
);

export const apodSlice = createSlice({
  name: 'data',
  initialState,
  reducers: {
    setData(state, { payload }) {
      state.apData = payload;
    },
    setNewDate(state, { payload }) {
      state.newDate = payload;
    },
    setInput(state, { payload }) {
      state.input = payload;
    },
    setFavorite(state, { payload }) {
      state.favorite = payload;
    },
  },
  extraReducers(builder) {
    builder
      .addCase(fetchFavorites.pending, (state) => {
        state.error = false;
        state.loading = true;
      })
      .addCase(fetchFavorites.fulfilled, (state, { payload }) => {
        state.error = false;
        state.loading = false;
        state.favorite = payload;
      })
      .addCase(fetchFavorites.rejected, (state) => {
        state.error = true;
        state.loading = false;
      });
  },
});

export const {
  setData, setNewDate, setInput, setFavorite,
} = apodSlice.actions;
export const getApod = (state: RootState) => state;
export default apodSlice.reducer;
