/* eslint-disable no-plusplus */
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const FavWrap = styled.div`
  width: 500px;
  height: 500px;
  border: 1px solid red;
  display: grid;
  grid-template-columns: 1fr 1fr;
`;
const Border = styled.div`
'`;

type dateData = {
  date: string,
}
type dataUrl = {
  url: string,
}
type dataTitle = {
  title: string,
}

type FavTypes = {
  date?: dateData,
  url?: dataUrl,
  title?: dataTitle,
  _id: string,
}
type favorite = {
  date: string,
  url: string,
  title: string,
  _id: string,
}

function Favorites() {
  const [favorites, setFavoirtes] = useState<favorite[]>([]);
  useEffect(() => {
    const fetchFavoirtes = async () => {
      const fav = await fetch('http://localhost:5000/favorites');
      const FavData = await fav.json();
      console.log(FavData);
      console.log(typeof FavData);
      const newFav = FavData.map((item: FavTypes) => {
        const favObj = {
          date: item.date?.date,
          url: item.url?.url,
          title: item.title?.title,
          _id: item._id,
        };
        console.log(favObj);
        return favObj;
      });
      console.log(newFav);
      setFavoirtes(newFav);
    };
    fetchFavoirtes();
  }, []);

  if (favorites) {
    console.log(favorites);
    console.log(typeof favorites);
  }
  console.log(Object.keys(favorites));
  return (
    <FavWrap>
      {Object.keys(favorites).length > 0 && Object.keys(favorites).map((value) => {
        const index = parseInt(value, 10);
        return (
          <div>
            <div>
              <p>{favorites[index].date}</p>
              <p>{favorites[index].url}</p>
              <p>{favorites[index].title}</p>
            </div>
          </div>
        );
      })}

    </FavWrap>
  );
}

export default Favorites;
