import { createSlice } from '@reduxjs/toolkit';
import { RootState } from '.';

type dataApodTypes = {
  data: {}
 }

type dataApod = {
  data: dataApodTypes;
  loading: boolean;
  error: boolean;
}

const initialState = {
  data: {},
  loading: true,
  error: false,
} as dataApod;

// export const fetchData = createAsyncThunk(
