  //       state.loading = true;
  //     })
  //     .addCase(fetchData.fulfilled, (state, { payload }) => {
  //       state.error = false;
  //       state.loading = false;
  //       state.data = payload;
  //     })
  //     .addCase(fetchData.rejected, (state) => {
  //       state.error = true;
  //       state.loading = false;
  //     });
  // },
});

export const { setData } = apodSlice.actions;
export const getApod = (state: RootState) => state;
export default apodSlice.reducer;
