  const [data, setData] = useState({});
  const [newDate, setNewDate] = useState('');

  useEffect(() => {
    axios
      .get(`https://api.nasa.gov/planetary/apod?date=${newDate}&api_key=${apiKey}`)
      .then((res) => {
        // console.log(res.data);
        setData(res.data);
        // console.log(data);
        setNewDate('2022-05-12');
      })
      .catch((err) => console.log(err));
  }, [newDate]);

